package javanew;

public class nestedfor_loop {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int c=0;
          for( int a=0; a<5; ++a)
          {
        	  for(int b=0; b<5;++b)
        	  {
        		  
        		  int d = ++c;
        		  System.out.println(d+" ");
        	  }
        	  System.out.println();
          
	       }
         
    }

}
